package com.ksg.operator;

public class ConcateString {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int a = 10;
		
		int b = 20;
		
		int c = 30;
		
		String d = "sadhu";
		
		System.out.println(a+b+c+d);
		System.out.println(d+a+c+b);
		System.out.println(b+d+c+a);
				
	

	}

}
